import React from 'react';
import './Film.css';

class Film extends React.Component {
    render(){
        return(
            <div className='Film'>
                <div className="Film-header">
                    <img src={this.props.film.imgSrc} alt={this.props.film.title} />
                    <h2 className='Film-name'>{this.props.film.title} ({this.props.film.year})</h2>
                    <p className='Film-genres'>{this.props.film.genres}</p>
                    <p className='Film-release'>{this.props.film.releaseDay}</p>
                </div>
                <div className='Film-information'>
                    <p className='Description'>{this.props.description}</p>
                    <p className='Rating'>{this.props.film.rating}</p> 
                </div>
            </div>
        );
    }
}

export default Film;
